      amount:1
    })
  }

  return (

    <div className={mealStyles.mealItem}>

      <div className={mealStyles['mealItem-imgConatainer']}>
          <img className={mealStyles['mealItem-img']} src={item.image} alt="meal-image" />
          {!itemCount ?
        <img src={assets.add_icon_white} className={mealStyles.add} onClick={()=>{setItemCount(prev=>prev+1);addItem(item)}} alt="Add" /> :
        <div className={mealStyles['foodItem-counter']}>
          <img onClick={()=>{setItemCount(prev=>prev-1);removeItem(item.id)}} src={assets.remove_icon_red} alt="remove" />
          <p>{itemCount}</p>
          <img onClick={()=>{setItemCount(prev=>prev+1);addItem(item)}} src={assets.add_icon_green} alt="add" />
        </div>  
        }
      </div>

      <div className={mealStyles['mealItem-info']}>

        <div className={mealStyles['mealItem-name']}>
          <p>{item.name}</p>
          <img  src={assets.rating_starts} alt="ratings" />
        </div>

      
        <p className={mealStyles['mealItem-desc']}>{item.description}</p>
        <p className={mealStyles['mealItem-price']}>${item.price}</p>

      </div>

    </div>
  );
};
export default MealsItem;
