import { useState } from "react";
import Input from "../UI/Input";
import styles from './MealForm.module.css'
const MealForm = () => {

    const [eneteredInput, setEnteredInput] = useState(0);


    const formHandler = (e) => {
        e.preventDefault();
        setEnteredInput(e.val)
    }

    return (

        <form onSubmit={formHandler} className={styles.form}>
            <Input 
                
                label="Amount"
            
                input={{
  
                type: "number",
                min:'1',
                max : '5',
                step: '1',
                defaultValue: '1'}}
            />

            <button>+Add</button>
        </form>

        );
}
export default MealForm;
