import Input from "../UI/Input";

const MealForm = () => {
    return (

        <>
            <Input 
                
                label="Amount"
            
                input={{
                id : "meal_"+ props.id,
                type: "number",
                min:'1',
                max : '5',
                step: '1',
                defaultValue: '1'}}
            />

            <button>+Add</button>
        </>

        );
}
export default MealForm;
