import React, { useReducer } from 'react'
import CartContext from './Cart_Context'
import { type } from '@testing-library/user-event/dist/type';
import { act } from 'react-dom/test-utils';

const defaultState = {
  items:[],
   totalAmount : 0
}

const cartReducer = (state, action) => {
  if (action.type === "ADD") {

    let updatedItems;
    const updatedAmount =state.totalAmount + action.item.price;
  
    const addItemIndex = state.items.findIndex((item) => 
      item.id === action.item.id
    );

    const addItem = state.items[addItemIndex];

    if (addItem) {
      const updatedItem = {
        ...addItem,
        amount: addItem.amount + action.item.amount
      }
      updatedItems = [...state.items]
      updatedItem[addItemIndex] = updatedItem;
    } else {
       updatedItems = state.items.concat(action.item);
    }

    return {
      items: updatedItems,
      totalAmount: updatedAmount
    }

  }


  if (action.type === "REMOVE") {
    let updatedItems;
    const exixtingItemIndex = state.items.findIndex((item) => item.id === action.id);
    const existingItem = state.items[exixtingItemIndex];

    const updatedAmount = state.totalAmount - existingItem.amount;

    if (existingItem.length === 1) {
      const updatedItem = [...state.items]
     updatedItems = updatedItem.filter(item=> item.id !== action.id)
    } else {
      
      const updatedItem = [{ ...existingItem, amount: existingItem.amount - 1 }]
      updatedItems = [state.items];
      updatedItems[exixtingItemIndex] = updatedItem;
    }

    return {
      items: updatedItems,
      totalAmount:updatedAmount
    }

  }

  return defaultState;


}

export default function CartProvider(props) {

  const [cartState, dispatchfn] = useReducer(cartReducer, defaultState);

  const addItemHandler = (item) => {
    dispatchfn({ type: "ADD", item: item });
  }
  const removeItemHandler = (id)=>{
    dispatchfn({ type: "REMOVE", id: id });
  }

  const cartContext = {
    item: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem:addItemHandler,
    removeItem: removeItemHandler
  };

  console.log(cartState.item);
  return (
      <CartContext.Provider value={cartContext}>
          {props.children}
  </CartContext.Provider>
  )
}
